mapper.update:
    	update t86_board 
    	set title = #{title}, 
		content = #{content}
    	where no = #{postNo}

mapper.select:
select no as postNo, title, writer, content, reg_date as regDate
from t86_board 
where no = #{postNo}

mapper.update:
update t86_board_reply
   set reply_content = #{rContent}
 where reply_no = #{replyNo}

mapper.select:
select id, pass, name, email 
from t86_board_user
where id = #{id}
and pass = #{pass}
